import React from "react";
import "/src/App.css";

const Modal = ({ text, top, visibility }) => {
  const modalStyle = {
    top: `${top}px`,
    display: visibility,
  };

  return (
    <div className="modal-overlay" style={{ display: visibility }}>
      <div className="modal-window" style={modalStyle}>
        <span className="modal-close">&times;</span>
        <p>{text}</p>
      </div>
    </div>
  );
};

export default Modal;
