import React from "react";

const Modal = ({ text, top, left, visibility }) => {
  const modalStyle = {
    top: `${top + 50}px`, // Positioning the modal underneath the button
    left: `${left + 50}px`, // Align the modal with the button
    display: visibility,
  };

  return (
    <div className="modal-window" style={modalStyle}>
      <p>{text}</p>
    </div>
  );
};

export default Modal;
