import React from "react";

const Button = ({ text, btnIndex, refFunction, setInfo, className }) => {
  return (
    <button
      className={className}
      ref={refFunction}
      onClick={() =>
        setInfo({ index: btnIndex, buttonName: text, visibility: "block" })
      }
    >
      {text}
    </button>
  );
};

export default Button;
