import React from "react";

const Modal = ({ text, top, visibility }) => {
  return (
    <div className={`modal-overlay ${visibility === "block" ? "active" : ""}`}>
      <div className="modal-window" style={{ top: `${top}px` }}>
        <span className="modal-close">&times;</span>
        <p>{text}</p>
      </div>
    </div>
  );
};

export default Modal;
