import React, { useState, useRef } from "react";
import "./App.css";
import Button from "./components/Button/Button";
import Modal from "./components/Modal/Modal";

function App() {
  const [info, setInfo] = useState({
    index: 0,
    buttonName: "",
    top: 0,
    left: 0,
    visibility: "none",
  });
  const btnRef = useRef([]);

  const handleButtonClick = (btnIndex, btnName, btnElement) => {
    const { top, left } = btnElement.getBoundingClientRect();
    setInfo({
      index: btnIndex,
      buttonName: btnName,
      top,
      left,
      visibility: "block",
    });
  };

  const btnNames = ["First", "Second", "Third", "Fourth"];
  return (
    <>
      <div className="button-container">
        <div className="button-column">
          {btnNames.map((btnName, btnIndex) => (
            <Button
              key={btnIndex}
              text={btnName}
              btnIndex={btnIndex}
              refFunction={(refElement) => {
                if (refElement) btnRef.current[btnIndex] = refElement;
              }}
              onClick={() =>
                handleButtonClick(btnIndex, btnName, btnRef.current[btnIndex])
              }
            />
          ))}
        </div>
        <Modal
          text={info.buttonName}
          top={info.top}
          left={info.left}
          visibility={info.visibility}
        />
      </div>
    </>
  );
}

export default App;
